import React from 'react';
import { Link } from 'react-router-dom';

const ComicCard = ({ comic, userRating, onRate }) => {
  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <button
          key={i}
          onClick={() => onRate && onRate(comic.id, i)}
          className={`text-2xl ${
            i <= (rating || 0) ? 'text-yellow-400' : 'text-gray-300'
          } hover:text-yellow-400 transition-colors`}
          disabled={!onRate}
        >
          â˜…
        </button>
      );
    }
    return stars;
  };

  return (
    <div className="comic-card">
      <div className="aspect-w-3 aspect-h-4">
        {comic.image_url ? (
          <img
            src={comic.image_url}
            alt={comic.title}
            className="w-full h-48 object-cover"
          />
        ) : (
          <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
            <span className="text-gray-500 text-4xl">ðŸ“–</span>
          </div>
        )}
      </div>
      
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-900 mb-2">
          {comic.title}
        </h3>
        
        <p className="text-sm text-gray-600 mb-2 line-clamp-3">
          {comic.description}
        </p>
        
        <div className="mb-2">
          <span className="inline-block bg-primary-100 text-primary-800 text-xs px-2 py-1 rounded-full">
            {comic.genre}
          </span>
        </div>
        
        {comic.characters && comic.characters.length > 0 && (
          <div className="mb-3">
            <p className="text-xs text-gray-500 mb-1">Characters:</p>
            <div className="flex flex-wrap gap-1">
              {comic.characters.slice(0, 3).map((character, index) => (
                <span
                  key={index}
                  className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded"
                >
                  {character}
                </span>
              ))}
              {comic.characters.length > 3 && (
                <span className="text-xs text-gray-500">
                  +{comic.characters.length - 3} more
                </span>
              )}
            </div>
          </div>
        )}
        
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-1">
            {renderStars(userRating)}
          </div>
          
          <Link
            to={`/comic/${comic.id}`}
            className="text-primary-600 hover:text-primary-800 text-sm font-medium"
          >
            View Details
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ComicCard;
