import React, { useState, useEffect } from 'react';
import { recommendationsAPI } from '../services/api';
import ComicCard from '../components/ComicCard';
import LoadingSpinner from '../components/LoadingSpinner';

const Recommendations = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async () => {
    try {
      const response = await recommendationsAPI.getRecommendations();
      setRecommendations(response.data);
    } catch (err) {
      setError('Failed to fetch recommendations');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="comic-loader mb-4"></div>
          <p className="comic-text text-xl">Finding your perfect comics...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="comic-alert-error p-6 text-center">
          <span className="text-2xl">ðŸ’¥ {error}</span>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">AI Recommendations</h1>
        <p className="mt-2 text-gray-600">
          Based on your reading history and ratings, here are comics we think you'll love.
        </p>
      </div>

      {recommendations.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-6xl mb-4">ðŸ¤–</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No recommendations yet</h3>
          <p className="text-gray-600 mb-4">
            Rate some comics to get personalized recommendations!
          </p>
          <a
            href="/browse"
            className="btn-primary"
          >
            Browse Comics
          </a>
        </div>
      ) : (
        <div className="space-y-8">
          {recommendations.map((recommendation, index) => (
            <div key={recommendation.comic.id} className="bg-white rounded-lg shadow-md p-6">
              <div className="flex items-start space-x-6">
                <div className="flex-shrink-0">
                  <span className="inline-flex items-center justify-center w-8 h-8 rounded-full bg-primary-100 text-primary-800 text-sm font-medium">
                    #{index + 1}
                  </span>
                </div>
                
                <div className="flex-1">
                  <div className="grid md:grid-cols-4 gap-6">
                    <div className="md:col-span-1">
                      <ComicCard comic={recommendation.comic} />
                    </div>
                    
                    <div className="md:col-span-3 flex flex-col justify-center">
                      <div className="mb-4">
                        <h3 className="text-xl font-semibold text-gray-900 mb-2">
                          {recommendation.comic.title}
                        </h3>
                        <p className="text-gray-600 mb-3">
                          {recommendation.comic.description}
                        </p>
                        
                        <div className="mb-3">
                          <span className="inline-block bg-primary-100 text-primary-800 text-sm px-3 py-1 rounded-full">
                            {recommendation.comic.genre}
                          </span>
                        </div>
                        
                        {recommendation.comic.characters && recommendation.comic.characters.length > 0 && (
                          <div className="mb-4">
                            <p className="text-sm text-gray-500 mb-2">Characters:</p>
                            <div className="flex flex-wrap gap-1">
                              {recommendation.comic.characters.slice(0, 5).map((character, idx) => (
                                <span
                                  key={idx}
                                  className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded"
                                >
                                  {character}
                                </span>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                      
                      <div className="bg-gray-50 rounded-lg p-4">
                        <h4 className="text-sm font-medium text-gray-900 mb-2">
                          Why we recommend this:
                        </h4>
                        <p className="text-sm text-gray-600">
                          {recommendation.explanation}
                        </p>
                        <div className="mt-2">
                          <span className="text-xs text-gray-500">
                            Similarity Score: {(recommendation.similarity_score * 100).toFixed(1)}%
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Recommendations;
